import React, { useState } from "react";
import * as XLSX from "xlsx";
import axios from "axios";

const DistanceCalculator = () => {
  const [excelData, setExcelData] = useState([]);
  const [results, setResults] = useState([]);

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();

    reader.onload = (e) => {
      const arrayBuffer = e.target.result;
      const workbook = XLSX.read(arrayBuffer, { type: "array" });
      const sheetName = workbook.SheetNames[0];
      const sheet = workbook.Sheets[sheetName];
      const data = XLSX.utils.sheet_to_json(sheet);
      setExcelData(data);
    };

    reader.readAsArrayBuffer(file); // Using readAsArrayBuffer instead of readAsBinaryString
  };


  const calculateDistances = async () => {
    const apiKey = "AIzaSyAnOJWvRw41bGmNw0zsIuO4a8ntGaPL4PE"; // Replace with your API key
    const distances = [];

    for (const row of excelData) {
      const { Location1, Location2, ArriveBy } = row; // Adjust columns to match your Excel file
      try {
        const response = await axios.get(
          `https://maps.googleapis.com/maps/api/distancematrix/json`,
          {
            params: {
              origins: Location1,
              destinations: Location2,
              key: apiKey,
              arrival_time: ArriveBy
                ? new Date(ArriveBy).getTime() / 1000
                : undefined,
            },
          }
        );
        const distanceInfo = response.data.rows[0].elements[0];
        distances.push({
          Location1,
          Location2,
          distance: distanceInfo.distance.text,
          duration: distanceInfo.duration.text,
        });
      } catch (error) {
        console.error("Error fetching distance:", error);
      }
    }

    setResults(distances);
  };

  return (
    <div>
      <h1>Driving Distance Calculator</h1>
      <input type="file" accept=".xlsx, .xls" onChange={handleFileUpload} />
      <button onClick={calculateDistances} disabled={!excelData.length}>
        Calculate Distances
      </button>
      {results.length > 0 && (
        <table border="1">
          <thead>
            <tr>
              <th>Location 1</th>
              <th>Location 2</th>
              <th>Distance</th>
              <th>Duration</th>
            </tr>
          </thead>
          <tbody>
            {results.map((result, index) => (
              <tr key={index}>
                <td>{result.Location1}</td>
                <td>{result.Location2}</td>
                <td>{result.distance}</td>
                <td>{result.duration}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default DistanceCalculator;
